(function () { tinymce.PluginManager.requireLangPack('vimeo'); tinymce.create('tinymce.plugins.VimeoPlugin', { init: function (ed, url) { ed.addCommand('mceVimeo', function () { ed.windowManager.open({ file: url + '/vimeo.htm', width: 290 + parseInt(ed.getLang('example.delta_width', 0)), height: 180 + parseInt(ed.getLang('example.delta_height', 0)), inline: 1 }, { plugin_url: url, some_custom_arg: 'custom arg' }) }); ed.addButton('vimeo', { title: 'vimeo.desc', cmd: 'mceVimeo', image: url + '/img/vimeo.gif' }); ed.onNodeChange.add(function (ed, cm, n) { var active = false; if (n.nodeName == 'IMG') { try { var src = n.attributes["src"].value; var alt = n.attributes["alt"].value; var regexRes = src.match("vi/([^&#]*)/0.jpg"); active = regexRes[1] === alt } catch (err) { } } cm.setActive('vimeo', active) }) }, createControl: function (n, cm) { return null }, getInfo: function () { return { longname: 'Vimeo plugin', author: 'puuublic.com', authorurl: 'http://puuublic.com', infourl: 'http://puuublic.com', version: "1.0"} } }); tinymce.PluginManager.add('vimeo', tinymce.plugins.VimeoPlugin) })();