tinyMCEPopup.requireLangPack();var YoutubeDialog={init:function(){var f=document.forms[0];f.youtubeURL.value=tinyMCEPopup.editor.selection.getContent({format:'text'})},insert:function(){var url=document.forms[0].youtubeURL.value;if(url===null){tinyMCEPopup.close();return}idtube=url.match("youtu(?:\.be|be\.com|be\.com\.br)/(?:.*v(?:/|=)|(?:.*/)?)([a-zA-Z0-9-_]+)");var idyoutube=idtube[1];if(idyoutube==false){tinyMCEPopup.close()}else{tinyMCEPopup.editor.execCommand('mceInsertContent',false,'<iframe width="600" height="480" src="http://www.youtube.com/embed/'+idyoutube+'" frameborder="0" allowfullscreen="allowfullscreen"></iframe>');tinyMCEPopup.close()}}};tinyMCEPopup.onInit.add(YoutubeDialog.init,YoutubeDialog);